"use client";

import { motion } from "framer-motion";
import { useCall } from "@/context/CallContext";
import { socket } from "@/lib/socket";
import { useEffect, useRef, useState } from "react";

export default function CallWindow() {
  const {
    isOpen,
    closeCall,
    inCall,
    remoteUserId,
    localStream,
    remoteStream,
    setLocalStream,
    setRemoteStream,
    setInCall,
    peerRef,     // ‚Üê use THIS peerRef (from context)
  } = useCall();

  const localVideoRef = useRef<HTMLVideoElement>(null);
  const remoteVideoRef = useRef<HTMLVideoElement>(null);

  const [chatInput, setChatInput] = useState("");
  const [messages, setMessages] = useState([]);


  /** ---------------------------
   *   INITIALIZE WEBRTC
   * -----------------------------
   */
  async function startCall() {
    const stream = await navigator.mediaDevices.getUserMedia({
      video: true,
      audio: true,
    });

    setLocalStream(stream);

    if (localVideoRef.current) {
      localVideoRef.current.srcObject = stream;
    }

    const peer = new RTCPeerConnection({
      iceServers: [{ urls: "stun:stun.l.google.com:19302" }],
    });

    peerRef.current = peer;

    // Send local ICE candidates to other user
    peer.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit("ice-candidate", {
          to: remoteUserId,
          candidate: event.candidate,
        });
      }
    };

    // When remote stream arrives
    peer.ontrack = (event) => {
      setRemoteStream(event.streams[0]);
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = event.streams[0];
      }
    };

    // Add local stream tracks
    stream.getTracks().forEach((track) => {
      peer.addTrack(track, stream);
    });

    // Offer SDP
    const offer = await peer.createOffer();
    await peer.setLocalDescription(offer);

    socket.emit("call-user", {
      offer,
      to: remoteUserId,
    });

    setInCall(true);
  }

  /** ---------------------------
   *   SOCKET SIGNALING
   * -----------------------------
   */
  useEffect(() => {
    socket.on("call-offer", async ({ offer, from }) => {
      if (!peerRef.current) {
        peerRef.current = new RTCPeerConnection({
          iceServers: [{ urls: "stun:stun.l.google.com:19302" }],
        });
      }

      const peer = peerRef.current;

      peer.ontrack = (event) => {
        setRemoteStream(event.streams[0]);
        if (remoteVideoRef.current) {
          remoteVideoRef.current.srcObject = event.streams[0];
        }
      };

      peer.onicecandidate = (event) => {
        if (event.candidate) {
          socket.emit("ice-candidate", {
            to: from,
            candidate: event.candidate,
          });
        }
      };

      // Local stream
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });

      setLocalStream(stream);

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      stream.getTracks().forEach((track) => {
        peer.addTrack(track, stream);
      });

      await peer.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await peer.createAnswer();
      await peer.setLocalDescription(answer);

      socket.emit("call-answer", {
        to: from,
        answer,
      });

      setInCall(true);
    });

    socket.on("call-answer", async ({ answer }) => {
      await peerRef.current?.setRemoteDescription(
        new RTCSessionDescription(answer)
      );
    });

    socket.on("ice-candidate", async ({ candidate }) => {
      try {
        await peerRef.current?.addIceCandidate(candidate);
      } catch (e) {
        console.error("ICE FAILED:", e);
      }
    });

    return () => {
      socket.off("call-offer");
      socket.off("call-answer");
      socket.off("ice-candidate");
    };
  }, []);

  /** ---------------------------
   *    CHAT MESSAGES
   * -----------------------------
   */
  useEffect(() => {
    socket.on("chat-message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.off("chat-message");
    };
  }, []);

  function sendMessage() {
    if (!chatInput.trim()) return;

    const msg = {
      text: chatInput,
      to: remoteUserId,
      fromMe: true,
    };

    socket.emit("chat-message", msg);

    setMessages((prev) => [...prev, msg]);
    setChatInput("");
  }

  if (!isOpen) return null;

  return (
    <motion.div
      drag
      className="fixed bottom-4 right-4 z-50 w-[450px] h-[600px] bg-white shadow-2xl rounded-xl border overflow-hidden"
    >
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white p-3 flex justify-between items-center">
        <span className="font-semibold">Live Consultation</span>
        <button
          onClick={closeWindow}
          className="hover:bg-white/20 px-2 py-1 rounded"
        >
          ‚úï
        </button>
      </div>

      {/* Video Area */}
      <div className="relative h-[55%] bg-black">
        <video
          ref={localVideoRef}
          autoPlay
          muted
          className="absolute bottom-2 right-2 w-32 h-24 bg-gray-900 rounded-lg border border-white/20"
        />

        <video
          ref={remoteVideoRef}
          autoPlay
          className="w-full h-full object-cover"
        />
      </div>

      {/* Chat */}
      <div className="h-[45%] flex flex-col">
        <div className="flex-1 p-3 overflow-y-auto space-y-2 bg-gray-50">
          {messages.map((msg, i) => (
            <div
              key={i}
              className={`p-2 rounded-lg max-w-[70%] ${
                msg.fromMe ? "ml-auto bg-blue-600 text-white" : "bg-gray-200"
              }`}
            >
              {msg.text}
            </div>
          ))}
        </div>

        <div className="flex p-2 border-t bg-white">
          <input
            value={chatInput}
            onChange={(e) => setChatInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            className="flex-1 px-3 py-2 border rounded-lg"
            placeholder="Type message..."
          />
          <button
            onClick={sendMessage}
            className="ml-2 px-4 py-2 bg-blue-600 text-white rounded-lg"
          >
            Send
          </button>
        </div>
      </div>
    </motion.div>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { io, Socket } from 'socket.io-client';
import { useSession } from 'next-auth/react';
import Button from '@/components/ui/Button';
import Card from '@/components/ui/Card';
import { doctorApi, patientApi } from '@/lib/axios';

interface Message {
    sender: string;
    message: string;
    timestamp: string;
}

interface ChatRoomProps {
    otherUserId: string;
    otherUserName: string;
    role: 'patient' | 'doctor';
}

export default function ChatRoom({ otherUserId, otherUserName, role }: ChatRoomProps) {
    const { data: session } = useSession();
    const [socket, setSocket] = useState<Socket | null>(null);
    const [messages, setMessages] = useState<Message[]>([]);
    const [inputMessage, setInputMessage] = useState('');
    const [aiEnabled, setAiEnabled] = useState(false);
    const messagesEndRef = useRef<HTMLDivElement>(null);

    // Scroll to bottom on new message
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    // Initialize Socket and AI Status
    useEffect(() => {
        if (!session?.user) return;

        // Connect to Socket.io
        const newSocket = io('http://localhost:5000');
        setSocket(newSocket);

        const roomId = [session.user.id, otherUserId].sort().join('-');

        newSocket.emit('joinRoom', {
            roomId,
            userId: session.user.id,
            role
        });

        newSocket.on('newMessage', (msg: Message) => {
            setMessages((prev) => [...prev, msg]);
        });

        // Fetch AI status if doctor
        if (role === 'doctor') {
            const fetchAiStatus = async () => {
                try {
                    const response = await doctorApi.get(`/ai/status/${session.user.id}`);
                    setAiEnabled(response.data.isAIEnabled);
                } catch (error) {
                    console.error('Error fetching AI status:', error);
                }
            };
            fetchAiStatus();
        }

        return () => {
            newSocket.disconnect();
        };
    }, [session, otherUserId, role]);

    const sendMessage = async () => {
        if (!inputMessage.trim() || !socket || !session?.user) return;

        const messageData = {
            sender: role, // 'patient' or 'doctor'
            message: inputMessage,
            timestamp: new Date().toISOString(),
            roomId: [session.user.id, otherUserId].sort().join('-'),
            receiverId: otherUserId
        };

        // Emit to socket
        socket.emit('sendMessage', messageData);

        // Add to local state immediately
        setMessages((prev) => [...prev, messageData]);
        setInputMessage('');

        // If patient sending to doctor, and AI is enabled (handled by backend usually, but user requested frontend logic)
        // The user request showed frontend logic for AI response:
        /*
        if (aiEnabled) {
             const response = await axios.post(`/ai/response/${doctorId}`, { query: message });
             ...
        }
        */
        // Since we are the sender, we don't know if the OTHER person has AI enabled unless we check.
        // If I am a patient, I don't know if doctor has AI enabled.
        // Ideally backend handles this. But following user's frontend-heavy logic:

        if (role === 'patient') {
            try {
                // Check if doctor has AI enabled (or just try to get response)
                // The user's code implies the PATIENT client calls the AI endpoint?
                // "Send message to AI for response if AI is enabled"
                // This seems to be client-side AI bot.

                // Let's try to call the AI response endpoint
                // We need to know if it's enabled first. 
                // But as a patient we might not have access to /ai/status/doctorId?
                // Let's assume we call /ai/response and if it returns something, we display it.

                const response = await patientApi.post(`/ai/response/${otherUserId}`, {
                    query: inputMessage,
                });

                if (response.data && response.data.response) {
                    const aiMsg = {
                        sender: 'ai',
                        message: response.data.response,
                        timestamp: new Date().toISOString()
                    };
                    setMessages((prev) => [...prev, aiMsg]);
                }
            } catch (error) {
                // AI might be disabled or error, just ignore
                console.log('AI response not available or error:', error);
            }
        }
    };

    const toggleAI = async () => {
        if (role !== 'doctor' || !session?.user) return;
        try {
            const newStatus = !aiEnabled;
            await doctorApi.patch('/ai/toggle', {
                doctorId: session.user.id,
                isAIEnabled: newStatus
            });
            setAiEnabled(newStatus);
        } catch (error) {
            console.error('Error toggling AI:', error);
        }
    };

    const handleVideoCall = () => {
        alert(`Starting video call with ${otherUserName}... (WebRTC implementation pending)`);
    };

    const handleAudioCall = () => {
        alert(`Starting audio call with ${otherUserName}... (WebRTC implementation pending)`);
    };

    return (
        <div className="flex flex-col h-[calc(100vh-120px)]">
            {/* Header */}
            <div className="bg-white p-4 border-b flex justify-between items-center shadow-sm rounded-t-lg">
                <div className="flex items-center gap-3">
                    <div className="h-10 w-10 rounded-full bg-blue-100 flex items-center justify-center text-xl">
                        {role === 'patient' ? 'üë®‚Äç‚öïÔ∏è' : 'üë§'}
                    </div>
                    <div>
                        <h2 className="font-bold text-gray-800">{otherUserName}</h2>
                        <p className="text-xs text-green-600 flex items-center gap-1">
                            <span className="h-2 w-2 rounded-full bg-green-500"></span> Online
                        </p>
                    </div>
                </div>

                <div className="flex gap-2">
                    {role === 'doctor' && (
                        <Button
                            variant={aiEnabled ? 'default' : 'outline'}
                            onClick={toggleAI}
                            className="mr-2"
                        >
                            {aiEnabled ? 'ü§ñ AI Auto-Reply On' : 'ü§ñ Enable AI'}
                        </Button>
                    )}
                    <Button variant="ghost" onClick={handleAudioCall} title="Audio Call">üìû</Button>
                    <Button variant="ghost" onClick={handleVideoCall} title="Video Call">üìπ</Button>
                </div>
            </div>

            {/* Messages Area */}
            <div className="flex-1 overflow-y-auto p-4 bg-gray-50 space-y-4">
                {messages.map((msg, idx) => {
                    const isMe = msg.sender === role;
                    const isAi = msg.sender === 'ai';
                    return (
                        <div key={idx} className={`flex ${isMe ? 'justify-end' : 'justify-start'}`}>
                            <div className={`max-w-[70%] rounded-lg p-3 ${isMe
                                    ? 'bg-blue-600 text-white rounded-br-none'
                                    : isAi
                                        ? 'bg-purple-100 text-purple-900 border border-purple-200'
                                        : 'bg-white text-gray-800 border border-gray-200 rounded-bl-none'
                                }`}>
                                {isAi && <p className="text-xs font-bold mb-1 text-purple-700">ü§ñ AI Assistant</p>}
                                <p>{msg.message}</p>
                                <p className={`text-[10px] mt-1 text-right ${isMe ? 'text-blue-100' : 'text-gray-400'}`}>
                                    {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                </p>
                            </div>
                        </div>
                    );
                })}
                <div ref={messagesEndRef} />
            </div>

            {/* Input Area */}
            <div className="bg-white p-4 border-t rounded-b-lg">
                <div className="flex gap-2">
                    <input
                        type="text"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                        placeholder="Type a message..."
                        className="flex-1 border rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                    <Button onClick={sendMessage}>Send</Button>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { patientApi, doctorApi } from "@/lib/axios";
import { socket } from "@/lib/socket";
import clsx from "clsx";

interface Conversation {
  _id: string;
  participants: { _id: string; name: string; role: string }[];
  lastMessage: string;
}

export default function ChatSidebar({
  role,
  onSelect,
}: {
  role: "doctor" | "patient";
  onSelect: (id: string, user: any) => void;
}) {
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const api = role === "doctor" ? doctorApi : patientApi;

  useEffect(() => {
    api.get("/chat/my").then((res) => setConversations(res.data));
  }, []);

  return (
    <div className="w-64 bg-green-800 border-r p-4 overflow-y-auto">
      <h2 className="font-semibold text-xl mb-4">Chats</h2>

      {conversations.map((c) => {
        const other = c.participants.find((p) => p.role !== role);

        return (
          <div
            key={c._id}
            onClick={() => onSelect(c._id, other)}
            className={clsx(
              "p-3 mb-2 rounded cursor-pointer bg-blue hover:bg-black-200"
            )}
          >
            <p className="font-semibold">{other?.name}</p>
            <p className="text-sm text-yellow line-clamp-1">
              {c.lastMessage}
            </p>
          </div>
        );
      })}
    </div>
  );
}

"use client";

import { useEffect, useState, useRef } from "react";
import { socket } from "@/lib/socket";
import { patientApi, doctorApi } from "@/lib/axios";
import clsx from "clsx";

interface Msg {
  _id: string;
  senderId: string;
  message: string;
  isAI?: boolean;
}

export default function ChatWindow({
  user,
  conversationId,
  role,
}: {
  user: any;
  conversationId: string;
  role: "doctor" | "patient";
}) {
  const [messages, setMessages] = useState<Msg[]>([]);
  const [input, setInput] = useState("");
  const api = role === "doctor" ? doctorApi : patientApi;
  const bottomRef = useRef(null);

  useEffect(() => {
    if (!conversationId) return;

    api.get(`/chat/${conversationId}/messages`).then((res) => {
      setMessages(res.data);
    });

    socket.on("message:receive", (msg) => {
      if (msg.conversationId === conversationId) {
        setMessages((prev) => [...prev, msg]);
      }
    });

    return () => {
      socket.off("message:receive");
    };
  }, [conversationId]);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  function send() {
    if (!input.trim()) return;

    const payload = {
      conversationId,
      from: socket.userId,
      to: user._id,
      message: input,
    };

    socket.emit("message:send", payload);

    setMessages((m) => [
      ...m,
      {
        _id: crypto.randomUUID(),
        senderId: socket.userId,
        message: input,
      },
    ]);

    setInput("");
  }

  return (
    <div className="flex flex-col h-full w-full bg-black">
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.map((m) => (
          <div
            key={m._id}
            className={clsx(
              "max-w-sm p-3 rounded-lg",
              m.senderId === socket.userId
                ? "bg-blue-500 text-white self-end"
                : m.isAI
                ? "bg-purple-500 text-red self-start"
                : "bg-black-200 text-white-800 self-start"
            )}
          >
            {m.message}
          </div>
        ))}

        <div ref={bottomRef}></div>
      </div>

      <div className="p-4 border-t flex space-x-2">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="flex-1 px-3 py-2 border rounded"
          placeholder="Type a message..."
        />
        <button
          onClick={send}
          className="bg-blue-600 text-white px-4 rounded"
        >
          Send
        </button>
      </div>
    </div>
  );
}

"use client";

import { createContext, useContext, useState, useRef } from "react";
import { socket } from "@/lib/socket";

interface CallContextType {
  isOpen: boolean;
  inCall: boolean;
  remoteUserId: string | null;

  localStream: MediaStream | null;
  remoteStream: MediaStream | null;

  openCall: (id: string) => void;
  closeCall: () => void;

  setLocalStream: (s: MediaStream) => void;
  setRemoteStream: (s: MediaStream) => void;
  setInCall: (v: boolean) => void;

  peerRef: React.MutableRefObject<RTCPeerConnection | null>;
}

const CallContext = createContext<CallContextType | null>(null);

export const CallProvider = ({ children }: { children: React.ReactNode }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [inCall, setInCall] = useState(false);
  const [remoteUserId, setRemoteUserId] = useState<string | null>(null);

  const [localStream, setLocalStream] = useState<MediaStream | null>(null);
  const [remoteStream, setRemoteStream] = useState<MediaStream | null>(null);

  const peerRef = useRef<RTCPeerConnection | null>(null);

  function openCall(id: string) {
    setIsOpen(true);
    setRemoteUserId(id);
  }

  function closeCall() {
    setIsOpen(false);
    setInCall(false);

    // Stop video streams
    localStream?.getTracks().forEach((t) => t.stop());
    remoteStream?.getTracks().forEach((t) => t.stop());

    setLocalStream(null);
    setRemoteStream(null);

    // Close peer connection
    if (peerRef.current) {
      peerRef.current.close();
      peerRef.current = null;
    }
  }

  return (
    <CallContext.Provider
      value={{
        isOpen,
        inCall,
        remoteUserId,

        localStream,
        remoteStream,

        openCall,
        closeCall,

        setLocalStream,
        setRemoteStream,
        setInCall,

        peerRef,
      }}
    >
      {children}
    </CallContext.Provider>
  );
};

export const useCall = () => useContext(CallContext)!;

